import React, { useState } from 'react';
import { LinkContainer } from 'react-router-bootstrap';
import { Nav, NavItem } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import SearchBox from './SearchBox';
import Product from './Product';
import HomeScreen from '../screens/HomeScreen';
import { SlDiamond } from 'react-icons/sl';

const ProductListPrice = ({ list }) => {
  const productList = useSelector((state) => state.productList);
  const { loading, error, products, page, pages } = productList;
  
  // Create a Set object to hold the unique categories
  const uniqueCategories = new Set(products.map((product) => product.category));
  
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');

  // Filter the products based on the selected price range
  const filterProductsByPrice = (min, max) => {
    // You can implement your own logic to filter the products
    const filteredProducts = products.filter(
      (product) => product.price >= min && product.price <= max
    );

    // Return the filtered products or an empty array if no products match the criteria
    return filteredProducts || [];
  };

  // Handle the search button click event
  const handleSearch = () => {
    const filteredProducts = filterProductsByPrice(minPrice, maxPrice);

    // Do something with the filtered products, such as updating the state or dispatching an action
    // ...

    // Clear the input fields after searching
    setMinPrice('');
    setMaxPrice('');
  };

  return (
    <div style={{ backgroundColor: '#f2f2f2', padding: '20px' }}>
      <h3 style={{ color: '#333', marginBottom: '20px' }}>Shop By Category</h3>
      <hr />
      <div className="search-container">
        <input
          type="number"
          placeholder="Min Price"
          value={minPrice}
          onChange={(e) => setMinPrice(e.target.value)}
        />
        <input
          type="number"
          placeholder="Max Price"
          value={maxPrice}
          onChange={(e) => setMaxPrice(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      <Nav className='mb-4'>
        {/* Iterate over the unique categories and render a link for each one */}
        {[...uniqueCategories].map((category) => (
          <Nav.Item key={category}>
            <LinkContainer to={`/search/${category}`}>
              <Nav.Link className='color' style={{ color: '#333', fontWeight: 'bold' }}>
                <SlDiamond /> {category}
              </Nav.Link>
            </LinkContainer>
          </Nav.Item>
        ))}
      </Nav>
    </div>
  );
};

export default ProductListPrice;
