class ActionProvider {
    constructor(createChatBotMessage) {
      this.createChatBotMessage = createChatBotMessage;
    }
  
    handleProducts = () => {
      const message = this.createChatBotMessage(
        'We offer a wide range of products, including rings, necklaces, bracelets, and earrings. Our products are made from high-quality materials and come in a variety of styles and designs.'
      );
      this.addMessageToBotState(message);
    };
  
    handleCustomJewelry = () => {
      const message = this.createChatBotMessage(
        'Yes, we offer custom jewelry! Our expert designers can create a one-of-a-kind piece that is tailored to your specific needs and preferences. To learn more about our custom jewelry services, please visit our website or contact us directly.'
      );
      this.addMessageToBotState(message);
    };
  
    handleServices = () => {
      const message = this.createChatBotMessage(
        'In addition to our products, we offer a variety of services to meet all of your jewelry needs. Our services include repair and restoration, resizing, and cleaning and maintenance. We also offer appraisals and consultations to help you make informed decisions about your jewelry.'
      );
      this.addMessageToBotState(message);
    };
  
    handleDefault = () => {
      const message = this.createChatBotMessage('Sorry, I did not understand that. Please try again.');
      this.addMessageToBotState(message);
    };
  
    addMessageToBotState = (message) => {
      this.setState((state) => ({
        ...state,
        messages: [...state.messages, message],
      }));
    };
  }
  
  export default ActionProvider;
