import React, { useState } from 'react';
import { LinkContainer } from 'react-router-bootstrap';
import { Nav, NavItem, Form, Button } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import SearchBox from './SearchBox';
import Product from './Product';
import HomeScreen from '../screens/HomeScreen';
import "./style.css"
import { SlDiamond } from 'react-icons/sl';

const CategoryList = ({ list }) => {
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');
  const productList = useSelector((state) => state.productList);
  const { loading, error, products, page, pages } = productList;
  const dispatch = useDispatch();

  const uniqueCategories = new Set(products.map((product) => product.category));

  const handlePriceSearch = (e) => {
    e.preventDefault();
    // Dispatch an action to filter products based on price range
    dispatch(filterProductsByPrice(minPrice, maxPrice));
  };

  return (
    <div style={{ backgroundColor: '#f2f2f2', padding: '20px' }}>
      <h3 style={{ color: '#333', marginBottom: '20px' }}>Shop By Category</h3>
      <hr />
      <Nav className='mb-4'>
        {[...uniqueCategories].map((category) => (
          <Nav.Item key={category}>
            <LinkContainer to={`/search/${category}`}>
              <Nav.Link className='color' style={{ color: '#333', fontWeight: 'bold' }}>
                <SlDiamond /> {category}
              </Nav.Link>
            </LinkContainer>
          </Nav.Item>
        ))}
      </Nav>
      <Form onSubmit={handlePriceSearch}>
        <Form.Group>
          <Form.Label>Price Range:</Form.Label>
          <div className="d-flex">
            <Form.Control
              type="number"
              placeholder="Min"
              value={minPrice}
              onChange={(e) => setMinPrice(e.target.value)}
            />
            <span className="mx-2">to</span>
            <Form.Control
              type="number"
              placeholder="Max"
              value={maxPrice}
              onChange={(e) => setMaxPrice(e.target.value)}
            />
            <Button type="submit" variant="primary" className="ml-2">Search</Button>
          </div>
        </Form.Group>
      </Form>
    </div>
  );
};

export default CategoryList;
