import React, { useState } from 'react';
import { LinkContainer } from 'react-router-bootstrap';
import { Nav } from 'react-bootstrap';
import { useSelector } from 'react-redux';
import { SlDiamond } from 'react-icons/sl';

const CategoryListByPrice = () => {
  const productList = useSelector((state) => state.productList);
  const { products } = productList;

  const uniqueCategories = new Set(products.map((product) => product.category));

  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');
  const [filteredCategories, setFilteredCategories] = useState([]);

  const filterCategoriesByPrice = (min, max) => {
    const filteredCategories = [...uniqueCategories].filter((category) => {
      const categoryProducts = products.filter(
        (product) => product.category === category && product.price >= min && product.price <= max
      );
      return categoryProducts.length > 0;
    });

    setFilteredCategories(filteredCategories);
  };

  const handleSearch = () => {
    filterCategoriesByPrice(minPrice, maxPrice);
  };

  return (
    <div style={{ backgroundColor: '#f2f2f2', padding: '20px' }}>
      <h3 style={{ color: '#333', marginBottom: '20px' }}>Shop By Category</h3>
      <hr />
      <div className="search-container">
        <input
          type="number"
          placeholder="Min Price"
          value={minPrice}
          onChange={(e) => setMinPrice(e.target.value)}
        />
        <input
          type="number"
          placeholder="Max Price"
          value={maxPrice}
          onChange={(e) => setMaxPrice(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      <Nav className="mb-4">
        {filteredCategories.length > 0 ? (
          filteredCategories.map((category) => (
            <Nav.Item key={category}>
              <LinkContainer to={`/search/${category}`}>
                <Nav.Link className="color" style={{ color: '#333', fontWeight: 'bold' }}>
                  <SlDiamond /> {category}
                </Nav.Link>
              </LinkContainer>
            </Nav.Item>
          ))
        ) : (
          <p>No categories found within the specified price range.</p>
        )}
      </Nav>
    </div>
  );
};

export default CategoryListByPrice;
