import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { SlDiamond } from 'react-icons/sl';
import { LinkContainer } from 'react-router-bootstrap';
import { Nav, NavItem } from 'react-bootstrap';
import { searchProducts } from 'path/to/searchAPI';
import { listProducts } from 'path/to/productActions';

const ProductListPrice = () => {
  const productList = useSelector((state) => state.productList);
  const { loading, error, products } = productList;

  const dispatch = useDispatch();

  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');
  const [filteredProducts, setFilteredProducts] = useState([]);

  const filterProductsByPrice = () => {
    const filteredProducts = products.filter(
      (product) => product.price >= minPrice && product.price <= maxPrice
    );
    setFilteredProducts(filteredProducts);
  };

  const handleSearch = async () => {
    filterProductsByPrice();
    try {
      const searchResults = await searchProducts(minPrice, maxPrice); // Assuming searchProducts function is implemented in the search API
      dispatch(listProducts(searchResults)); // Update the product list with search results
    } catch (error) {
      // Handle error
    }
  };

  useEffect(() => {
    // Fetch initial product list
    dispatch(listProducts()); // Assuming listProducts action is implemented to fetch the product list
  }, [dispatch]);

  // Create a Set object to hold the unique categories
  const uniqueCategories = new Set(products.map((product) => product.category));

  return (
    <div style={{ backgroundColor: '#f2f2f2', padding: '20px' }}>
      <h3 style={{ color: '#333', marginBottom: '20px' }}>Shop By Category</h3>
      <hr />
      <Nav className='mb-4'>
        {/* Iterate over the unique categories and render a link for each one */}
        {[...uniqueCategories].map((category) => (
          <Nav.Item key={category}>
            <LinkContainer to={`/search/${category}`}>
              <Nav.Link className='color' style={{ color: '#333', fontWeight: 'bold' }}>
                <SlDiamond /> {category}
              </Nav.Link>
            </LinkContainer>
          </Nav.Item>
        ))}
      </Nav>

      <h3 style={{ color: '#333', marginBottom: '20px' }}>Search By Price</h3>
      <div className="search-container">
        <input
          type="number"
          placeholder="Min Price"
          value={minPrice}
          onChange={(e) => setMinPrice(Number(e.target.value))}
        />
        <input
          type="number"
          placeholder="Max Price"
          value={maxPrice}
          onChange={(e) => setMaxPrice(Number(e.target.value))}
        />
        <button onClick={handleSearch}>Search</button>
      </div>

      {filteredProducts.length > 0 ? (
        <div>
          {filteredProducts.map((product) => (
            <div key={product.name}>
              <h4>{product.name}</h4>
              <p>{product.description}</p>
              <p>Price: {product.price}</p>
            </div>
          ))}
        </div>
      ) : (
        <p>No products found within the specified price range.</p>
      )}
    </div>
  );
};

export default ProductListPrice;
