import React from 'react';
import { useSelector } from 'react-redux';
import { Card, Col, Row } from 'react-bootstrap';
import { Link } from 'react-router-dom'; // Import Link from React Router

const TopRatedProducts = () => {
  const productList = useSelector((state) => state.productList);
  const { products } = productList;

  const sortedProducts = products
    .filter((product) => product.numReviews > 0)
    .slice()
    .sort((a, b) => b.rating - a.rating);

  return (
    <div style={{ padding: '20px' }}>
      <h3 style={{ color: '#333', marginBottom: '20px' }}>Top Rated Products</h3>
      <hr />
      <Row>
        {sortedProducts.map((product) => (
          <Col key={product._id} md={4} className="mb-4">
            <Card>
              <Link to={`/products/${product._id}`}>{/* Use Link instead of anchor tag */}
                <Card.Img variant="top" src={product.image} alt={product.name} />
                <Card.Body>
                  <Card.Title>{product.name}</Card.Title>
                  <Card.Text>{product.description}</Card.Text>
                  <Card.Text>Brand: {product.brand}</Card.Text>
                  <Card.Text>Category: {product.category}</Card.Text>
                  <Card.Text>Price: ${product.price}</Card.Text>
                  <Card.Text>Rating: {product.rating}</Card.Text>
                  <Card.Text>Number of Reviews: {product.numReviews}</Card.Text>
                </Card.Body>
              </Link>
            </Card>
          </Col>
        ))}
      </Row>
    </div>
  );
};

export default TopRatedProducts;
