import React, { useState } from 'react'
import { Form, Button,  FormControl,
  InputGroup } from 'react-bootstrap'

const SearchBox = ({ history }) => {

  const [keyword, setKeyword] = useState('')

  const submitHandler = (e) => {
    e.preventDefault()
    if (keyword.trim()) {
      history.push(`/search/${keyword}`)
    } else {
      history.push('/')
    }
  }

  return (
    <Form onSubmit={submitHandler} inline className="d-flex">


      <InputGroup>
              <InputGroup.Text className="bg-white">
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  viewBox="0 0 24 24"
                  width="16"
                  height="16"
                  fill="currentColor"
                >

                </svg>
              </InputGroup.Text>
              <FormControl type="search" className="me-2" placeholder="Search"
              name='q'
              onChange={(e) => setKeyword(e.target.value)}         
              />
              <Button type='submit' variant="outline-danger" className='p-2'> Search</Button>
            </InputGroup>

    </Form>
  )
}

export default SearchBox
