import React, { useState } from 'react';
import "chat.css"
function Chatbot() {
  const [isOpen, setIsOpen] = useState(false);

  const toggleChatbot = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div>
      <button id="toggle-button" onClick={toggleChatbot}>
        Toggle Chatbot
      </button>
      {isOpen && (
        <iframe
          width="350"
          height="430"
          allow="microphone;"
          src="https://console.dialogflow.com/api-client/demo/embedded/1555eb45-e274-44e8-a7c0-fd6515fd47cc"
        ></iframe>
      )}
    </div>
  );
}

export default Chatbot;
