import React from 'react';
import { Chatbot } from 'react-chatbot-kit';
import { MessageParser } from './MessageParser';
import { ActionProvider } from './ActionProvider';
import { Container, Row, Col, Button } from 'react-bootstrap';

const config = {
  botName: 'Jewelry Store Chatbot',
  initialMessages: [
    {
      id: 'intro',
      message: 'Hi there! How can I help you today?',
      trigger: 'options',
    },
    {
      id: 'options',
      options: [
        {
          value: 'What kind of jewelry do you sell?',
          label: 'What kind of jewelry do you sell?',
          trigger: 'product-types',
        },
        {
          value: 'How do I care for my jewelry?',
          label: 'How do I care for my jewelry?',
          trigger: 'care-info',
        },
        {
          value: 'Do you offer custom jewelry?',
          label: 'Do you offer custom jewelry?',
          trigger: 'custom-jewelry-info',
        },
        {
          value: 'Something else',
          label: 'Something else',
          trigger: 'fallback',
        },
      ],
    },
    {
      id: 'product-types',
      message: 'We sell a variety of jewelry including rings, necklaces, bracelets, and earrings.',
      trigger: 'options',
    },
    {
      id: 'care-info',
      message: 'To care for your jewelry, we recommend keeping it away from moisture and chemicals, and storing it in a jewelry box or bag when not in use.',
      trigger: 'options',
    },
    {
      id: 'custom-jewelry-info',
      message: 'Yes, we do offer custom jewelry! Please visit our website or contact us for more information.',
      trigger: 'options',
    },
    {
      id: 'fallback',
      message: 'I\'m sorry, I didn\'t understand that. Could you please rephrase your question?',
      trigger: 'options',
    },
  ],
};

const ChatbotComponent = () => {
  return (
    <Container>
      <Row>
        <Col md={{ span: 6, offset: 6 }} className="fixed-bottom">
          <Chatbot
            config={config}
            messageParser={MessageParser}
            actionProvider={ActionProvider}
            widgetStyle={{
              position: 'absolute',
              bottom: '0',
              right: '0',
              borderRadius: '10px',
              boxShadow: '0px 3px 5px rgba(0, 0, 0, 0.2)',
              padding: '10px',
            }}
          />
        </Col>
      </Row>
    </Container>
  );
};

export default ChatbotComponent;
