import React, { useState } from 'react';
import { LinkContainer } from 'react-router-bootstrap';
import { Nav, NavItem } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import SearchBox from './SearchBox';
import Product from './Product';
import HomeScreen from '../screens/HomeScreen';
import "./style.css";
import { SlDiamond } from 'react-icons/sl';

const ProductListPrice = ({ list }) => {
  const productList = useSelector((state) => state.productList);
  const { loading, error, products, page, pages } = productList;
  
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');

  // Create a Set object to hold the unique categories
  const uniqueCategories = new Set(products.map((product) => product.category));

  // Filter the categories based on the selected price range
  const filteredCategories = [...uniqueCategories].filter((category) => {
    const categoryProducts = products.filter((product) => product.category === category);
    const categoryPrices = categoryProducts.map((product) => product.price);
    const min = Math.min(...categoryPrices);
    const max = Math.max(...categoryPrices);
    return min >= minPrice && max <= maxPrice;
  });

  return (
    <div style={{ backgroundColor: '#f2f2f2', padding: '20px' }}>
      <h3 style={{ color: '#333', marginBottom: '20px' }}>Shop By Category</h3>
      <hr />
      <div className="price-filter">
        <input
          type="number"
          placeholder="Min Price"
          value={minPrice}
          onChange={(e) => setMinPrice(e.target.value)}
        />
        <input
          type="number"
          placeholder="Max Price"
          value={maxPrice}
          onChange={(e) => setMaxPrice(e.target.value)}
        />
      </div>
      <Nav className='mb-4'>
        {/* Iterate over the filtered categories and render a link for each one */}
        {filteredCategories.map((category) => (
          <Nav.Item key={category}>
            <LinkContainer to={`/search/${category}`}>
              <Nav.Link className='color' style={{ color: '#333', fontWeight: 'bold' }}>
                <SlDiamond /> {category}
              </Nav.Link>
            </LinkContainer>
          </Nav.Item>
        ))}
      </Nav>
    </div>
  );
};

export default ProductListPrice;
