import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { searchProductsByPrice } from '../api'; // Import your search API function

const SearchByPrice = () => {
  const dispatch = useDispatch();

  const productList = useSelector((state) => state.productList);
  const { loading, error, products } = productList;

  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');
  const [filteredProducts, setFilteredProducts] = useState([]);

  useEffect(() => {
    if (minPrice !== '' && maxPrice !== '') {
      dispatch(searchProductsByPrice(minPrice, maxPrice)); // Dispatch the action to search products by price
    }
  }, [dispatch, minPrice, maxPrice]);

  const handleSearch = () => {
    if (minPrice !== '' && maxPrice !== '') {
      dispatch(searchProductsByPrice(minPrice, maxPrice)); // Dispatch the action to search products by price
    }
  };

  return (
    <div style={{ backgroundColor: '#f2f2f2', padding: '20px' }}>
      <h3 style={{ color: '#333', marginBottom: '20px' }}>Search By Price</h3>
      <div className="search-container">
        <input
          type="number"
          placeholder="Min Price"
          value={minPrice}
          onChange={(e) => setMinPrice(Number(e.target.value))}
        />
        <input
          type="number"
          placeholder="Max Price"
          value={maxPrice}
          onChange={(e) => setMaxPrice(Number(e.target.value))}
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p>Error: {error}</p>
      ) : filteredProducts.length > 0 ? (
        <div>
          {filteredProducts.map((product) => (
            <div key={product.name}>
              <h4>{product.name}</h4>
              <p>{product.description}</p>
              <p>Price: {product.price}</p>
            </div>
          ))}
        </div>
      ) : (
        <p>No products found within the specified price range.</p>
      )}
    </div>
  );
};

export default SearchByPrice;
