import React, { useState } from 'react';
import { Chatbot } from 'react-chatbot-kit';
import { MessageParser } from './MessageParser';
import { ActionProvider } from './ActionProvider';
import { Container, Row, Col, Button } from 'react-bootstrap';

const config = {
  botName: 'Jewelry Store Chatbot',
  initialMessages: [
    {
      id: 'intro',
      message: 'Welcome to our jewelry store! How can I help you?',
      trigger: 'menu',
    },
    {
      id: 'menu',
      options: [
        {
          value: 'Tell me about your products',
          label: 'Tell me about your products',
          trigger: 'products',
        },
        {
          value: 'Do you offer custom jewelry?',
          label: 'Do you offer custom jewelry?',
          trigger: 'custom-jewelry',
        },
        {
          value: 'What services do you offer?',
          label: 'What services do you offer?',
          trigger: 'services',
        },
      ],
    },
    {
      id: 'products',
      message: 'We offer a wide range of products, including rings, necklaces, bracelets, and earrings. Our products are made from high-quality materials and come in a variety of styles and designs. What else can I help you with?',
      trigger: 'menu',
    },
    {
      id: 'custom-jewelry',
      message: 'Yes, we offer custom jewelry! Our expert designers can create a one-of-a-kind piece that is tailored to your specific needs and preferences. To learn more about our custom jewelry services, please visit our website or contact us directly.',
      trigger: 'menu',
    },
    {
      id: 'services',
      message: 'In addition to our products, we offer a variety of services to meet all of your jewelry needs. Our services include repair and restoration, resizing, and cleaning and maintenance. We also offer appraisals and consultations to help you make informed decisions about your jewelry. What else can I help you with?',
      trigger: 'menu',
    },
  ],
};

const ChatbotComponent = () => {
  return (
    <Container>
      <Row>
        <Col md={{ span: 6, offset: 6 }} className="fixed-bottom">
          <Chatbot
            config={config}
            messageParser={MessageParser}
            actionProvider={ActionProvider}
            widgetStyle={{
              position: 'absolute',
              bottom: '0',
              right: '0',
              borderRadius: '10px',
              boxShadow: '0px 3px 5px rgba(0, 0, 0, 0.2)',
              padding: '10px',
            }}
          />
        </Col>
      </Row>
    </Container>
  );
};

export default ChatbotComponent;
