import { createChatBotMessage } from 'react-chatbot-kit';

class ActionProvider {
  constructor(createChatBotMessage, setStateFunc) {
    this.createChatBotMessage = createChatBotMessage;
    this.setState = setStateFunc;
  }

  greet() {
    const message = this.createChatBotMessage('Hi, how can I help you?');
    this.updateChatbotState(message);
  }

  handleProductTypes = () => {
    const message = this.createChatBotMessage('We sell a variety of jewelry including rings, necklaces, bracelets, and earrings.');
    this.updateChatbotState(message);
  }

  handleCareInfo = () => {
    const message = this.createChatBotMessage('To care for your jewelry, we recommend keeping it away from moisture and chemicals, and storing it in a jewelry box or bag when not in use.');
    this.updateChatbotState(message);
  }

  handleCustomJewelryInfo = () => {
    const message = this.createChatBotMessage('Yes, we do offer custom jewelry! Please visit our website or contact us for more information.');
    this.updateChatbotState(message);
  }

  handleFallback = () => {
    const message = this.createChatBotMessage("I'm sorry, I didn't understand that. Could you please rephrase your question?");
    this.updateChatbotState(message);
  }

  updateChatbotState(message) {
    // Adds the message to the state of the chatbot
    this.setState(prevState => ({
      ...prevState,
      messages: [...prevState.messages, message],
    }));
  }
}

export default ActionProvider;
