class MessageParser {
    constructor(actionProvider) {
      this.actionProvider = actionProvider;
    }
  
    parse(message) {
      const lowerCaseMessage = message.toLowerCase();
  
      if (lowerCaseMessage.includes('product') || lowerCaseMessage.includes('ring') || lowerCaseMessage.includes('necklace') || lowerCaseMessage.includes('bracelet') || lowerCaseMessage.includes('earring')) {
        this.actionProvider.handleProducts();
      } else if (lowerCaseMessage.includes('custom') || lowerCaseMessage.includes('design') || lowerCaseMessage.includes('tailored')) {
        this.actionProvider.handleCustomJewelry();
      } else if (lowerCaseMessage.includes('service') || lowerCaseMessage.includes('repair') || lowerCaseMessage.includes('restoration') || lowerCaseMessage.includes('resize') || lowerCaseMessage.includes('clean') || lowerCaseMessage.includes('maintenance') || lowerCaseMessage.includes('appraisal') || lowerCaseMessage.includes('consultation')) {
        this.actionProvider.handleServices();
      } else {
        this.actionProvider.handleDefault();
      }
    }
  }
  
  export default MessageParser;
